import React, { Component } from "react";
import { connect } from "react-redux";
import {
  getFilterValue,
  addContact,
  deleteItem,
} from "./components/redux/contactActions";
import List from "./components/List/List";
import Phonebook from "./components/Phonebook";

class App extends Component {
  componentDidMount() {
    const local = localStorage.getItem("contacts");
    if (local !== null) {
      this.setState({ contacts: JSON.parse(local) });
    }
  }

  componentDidUpdate(prevState) {
    const { contacts } = this.state;
    if (prevState.contacts !== contacts) {
      localStorage.setItem("contacts", JSON.stringify(contacts));
    }
  }
  componentDidMount() {
    this.setState({
      contacts: JSON.parse(localStorage.getItem("contacts")) || [],
    });
  }
  filterList = () => {
    return this.props.contacts.filter((contact) =>
      contact.contact.toLowerCase().includes(this.props.filter)
    );
  };
  render() {
    return (
      <>
        <Phonebook
          getFilterValue={this.props.getFilterValue}
          ContactInfo={this.props.addContact}
          contacts={this.filterList()}
        />
        <List
          contacts={this.filterList()}
          filter={this.props.filter}
          FilterValue={this.props.getFilterValue}
          deleteItem={this.props.deleteItem}
        />
      </>
    );
  }
}
const mapStateProps = (state) => ({
  contacts: state.contacts,
  filter: state.filter,
});

export default connect(mapStateProps, {
  getFilterValue,
  addContact,
  deleteItem,
})(App);
