import { FilterValue, addContact, deleteItem } from "./contactActions";

const initialState = {
  contacts: [
    { id: "id-1", contact: "Rosie Simpson", number: "459-12-56" },
    { id: "id-2", contact: "Hermione Kline", number: "443-89-12" },
    { id: "id-3", contact: "Eden Clements", number: "645-17-79" },
    { id: "id-4", contact: "Annie Copeland", number: "227-91-26" },
  ],
  filter: "",
};

const contactReducer = (state = { ...initialState }, { type, payload }) => {
  switch (type) {
    case FilterValue.type:
      return { ...state, filter: payload };
    case addContact.type:
      return { ...state, contacts: [...state.contacts, payload] };
    case deleteItem.type:
      return {
        ...state,
        contacts: [
          ...state.contacts.filter((contact) => contact.id !== payload),
        ],
      };
    default:
      return state;
  }
};
export default contactReducer;
